
module.exports = async function createPayment ({ payment_id, sale_id, amount, khipuPayment }, pgClient) {
  const {} = khipuPayment
  await pgClient.query(`
    WITH payment (payment_id) AS (
      INSERT INTO accounting.payment(
        payment_id,
        amount
      ) VALUES ($2, $3)
      RETURNING payment_id
    ) INSERT INTO webstore.sale_payment (sale_id, payment_id)
    VALUES ($1, $2)
  `, [
    sale_id,
    payment_id,
    amount,
    // khipu params
  ])
}
