const pg = require('utils/pg')

module.exports = async function ({ payment_id }, pgClient = null) {
  const db = pgClient === null ? pg : pgClient

  const { rows: [ payment ] } = await db.query(/*sql*/`
    SELECT *
    FROM accounting.payment
    WHERE accounting.payment.payment_id = $1
  `, [ payment_id ])

  return payment
}
