const pg = require('utils/pg')

module.exports = async function getPaymenKhipuId({ payment_id },  pgClient = null) {
  const db = pgClient === null ? pg : pgClient

  const { rows: [ { khipu_payment_id } ] } = await db.query(/*sql*/`
    SELECT khipu_payment_id
    FROM accounting.payment
    WHERE accounting.payment.payment_id = $1
  `, [ payment_id ])

  return khipu_payment_id
}
