const pg = require('utils/pg')

module.exports = async function ({ payment_id }, pgClient = null) {
  const db = pgClient === null ? pg : pgClient

  const { rows: status } = await db.query(/*sql*/`
    SELECT *
    FROM accounting.payment_update
    WHERE accounting.payment_update.payment_id = $1
    ORDER BY created_at ASC
  `, [ payment_id ])

  return status
}

